mapper.select:
select * from parking
where car_num = #{car_num}
and state = 'Y'

select p.*, a.amount from (select row_number() over(order by pid desc) rnum, p.* from parking p where car_num like '%' || #{car_num} || '%'
and in_time like '%' || #{in_time} || '%'
) p left join amount a on a.aid = p.pid where rnum >= #{startNo} and rnum <= #{endNo} order by pid desc

select s.*, p.car_num, p.in_time from pspace s, parking p
where s.sid = #{sid}
and s.sid = p.pspace_id

mapper.update:
update parking
set dc_time = #{dc_time}
where pid = #{pid}
